CREATE DATABASE BookManagement

CREATE TABLE Role(
role_id int identity(1,1) PRIMARY KEY,
authority nvarchar(50),
);

CREATE TABLE [User](
id int identity(1,1) PRIMARY KEY,
user_name nvarchar(50),
password nvarchar(20),
role_id int FOREIGN KEY REFERENCES Role(role_id)
);

CREATE TABLE BookCase(
book_case_id int identity(1,1) PRIMARY KEY,
id int FOREIGN KEY REFERENCES [User](id),
book_case_name nvarchar(100)
);

CREATE TABLE Book(
book_id int identity(1,1) PRIMARY KEY,
book_title nvarchar(100),
author nvarchar(50),
brief text,
publisher nvarchar(100),
content text,
category nvarchar(100)
);

CREATE TABLE Contain(
book_case_id int FOREIGN KEY REFERENCES BookCase(book_case_id), 
book_id int FOREIGN KEY REFERENCES Book(book_id), 
create_date datetime
PRIMARY KEY(book_case_id, book_id)
);

